mapper.select:
SELECT GROUP_NO, GROUP_NAME, REGION_NAME, CATEGORY_NAME, GROUP_IMG, GROUP_NAME, GROUP_DETAIL, GROUP_TYPE, DECODE(GROUP_TYPE, 'A', '공개', 'B', '그룹명 공개', 'C', '비공개') AS GROUP_TYPE_STR, GROUP_DATE, MY_GROUP, APPLYING, MEMBER_COUNT FROM GROUP_ROOM R JOIN REGION_CATEGORY USING(REGION_NO) JOIN GROUP_CATEGORY USING(CATEGORY_NO) LEFT JOIN (SELECT GROUP_NO, 'Y' AS MY_GROUP FROM GROUP_MEMBER WHERE USER_ID = #{userId}) USING(GROUP_NO) LEFT JOIN (SELECT GROUP_NO, 'A' AS APPLYING FROM GROUP_JOIN_APPLY WHERE USER_ID = #{userId}) USING(GROUP_NO) LEFT JOIN (SELECT GROUP_NO, COUNT(*) AS MEMBER_COUNT FROM GROUP_MEMBER GROUP BY GROUP_NO) USING(GROUP_NO)
WHERE CATEGORY_NO = #{categoryNo}
ORDER BY GROUP_NO DESC

mapper.update:
UPDATE
       GROUP_ROOM
   SET
       GROUP_TYPE = #{groupType}
 WHERE 
       GROUP_NO = #{groupNo}

mapper.delete:
DELETE
       GROUP_JOIN_APPLY
 WHERE
       GROUP_NO = #{groupNo}
   AND
       USER_ID = #{userId}
